import React from 'react';
import { FaUserCircle, FaPhone, FaSignOutAlt, FaCaretDown } from 'react-icons/fa';
import { useAuth } from '../Authentication/AuthContext';
import { useNavigate } from 'react-router-dom';

const ProfileMenu = ({toastRef}) => {

  const { logout } = useAuth();
  const navigate = useNavigate();
    const handleLogout = (e) =>{
        
        e.preventDefault();
        Axios_reques("get",
          "/logout",
          {}
        ).then(response => {
          console.log(response.data);
          logout();
          // alert('User logout successfully!');
          navigate('/login');
          toastRef.current.showToast('Logout successful..!');
        })
        .catch(error => {
          // console.log(error.data);
          //console.error('Error registering user:', error);
          alert('Error logging out user!');
        });
      };

  return (
    <Dropdown>
      <Dropdown.Toggle
        as="div"
        id="dropdown-custom-components"
        className="d-flex align-items-center"
        style={{ 
          border: 'none', 
          background: 'transparent', 
          cursor: 'pointer', 
          padding: 0,
          position: 'relative'
        }}
      >
        <FaUserCircle size={24} /> {/* Profile Icon */}
        <FaCaretDown 
          style={{ 
            marginLeft: '5px',
            position: 'absolute',
            right: '0', 
            top: '50%', 
            transform: 'translateY(-50%)' 
          }} 
        /> {/* Custom Dropdown Arrow */}
      </Dropdown.Toggle>

      <Dropdown.Menu>
        <Dropdown.Item href="#/profile">
          <FaUserCircle className="mr-2" /> Profile
        </Dropdown.Item>
        <Dropdown.Item href="#/profile">
          <FaUserCircle className="mr-2" /> Your Contacts
        </Dropdown.Item>
        <Dropdown.Item href="#/contact">
          <FaPhone className="mr-2" /> Contact Us
        </Dropdown.Item>
        <Dropdown.Item onClick={handleLogout}>
          <FaSignOutAlt className="mr-2" /> Logout
        </Dropdown.Item>
      </Dropdown.Menu>
    </Dropdown>
  );
};

export default ProfileMenu;
